#!/usr/bin/env node

// main.js
( function() {

	var BitBundler = require( '../lib/bitbundler' )
	,	args = BitBundler.mapCommand( process.argv );


	/**
	* Do we have such command available
	*/
	if ( args ) {

		// Create the whole object
		if ( args[ 1 ] == "object" ) {
			return BitBundler.generateObject( args );
		}

		// Run The command
		BitBundler.runCommand( args );

	}

	else {
		console.log( "Usage: " );
		console.log( "bitbundler generate <php|js> [type] <name>" );
	}






}).call( this );
